    int2ext <- c(int2ext,
                 "Price|Energy Service|Transport LDV (US$2017/GJ)"         = "CES_input|fepet (EJ/yr)",  ## TODO: check units
                 "Price|Energy Service|Transport nonLDV (US$2017/GJ)"      = "CES_input|fedie (EJ/yr)"  ## TODO: check units
    )
